package com.scw.webui.config;

import java.io.FileWriter;
import java.io.IOException;

public class AlipayConfig {
	public static String app_id = "2016101800715307";
	public static String merchant_private_key = "";
	public static String alipay_public_key = "";
	public static String notify_url = "http://47.116.5.173:10000/order/returnurl";
	public static String return_url = "http://47.116.5.173:10000/order/notifyurl";
	public static String sign_type = "RSA2";
	public static String charset = "utf-8";
	public static String gatewayUrl = "https://openapi.alipaydev.com/gateway.do";
	public static String log_path = "";

	public static void logResult(String sWord) {
		FileWriter writer = null;
		try {
			writer = new FileWriter(log_path + "alipay_log_" + System.currentTimeMillis() + ".txt");
			writer.write(sWord);
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			if (writer != null) {
				try {
					writer.close();
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
		}
	}
}